import React from 'react'

function printStar(rating) {
    let star = "";
    for (let i = 0; i < rating; i++) {
        star += "ðŸ‘";
    }
    return star;
}
function Informasi({judul,tahunRilis,originalNetwork,aired,episode,rating,synopsis,genre,studio}) {
  return (
    <>
        <h1>{judul} ({tahunRilis})</h1>
        <h4>Original Network: {originalNetwork}</h4>
        {/* {
            originalNetwork.map((orinet) => {
                return (
                    <>
                        {orinet=="tvN" && <img src="data:image/png;base64," className='logo'></img>}
                        {orinet=="TVN" && <img src="data:image/png;base64," className='logo'></img>}
                        {orinet=="Netflix" && <img src="data:image/png;base64," className='logo'></img>}
                        {orinet=="ENA" && <img src="data:image/png;base64," className='logo'></img>}
                        {orinet=="JTBC" && <img src="https://upload.wikimedia.org/wikipedia/commons/a/aa/JTBC_logo.svg" className='logo'></img>}
                        {orinet=="KBS2" && <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/26/KBS_2_logo.svg/1200px-KBS_2_logo.svg.png" className='logo'></img>}
                        {orinet=="SBS" && <img src="data:image/png;base64," className='logo'></img>}
                    </>
                )
            })
        }    */}
        <h4>{printStar(rating)} ({rating})</h4> 
        <div className="mt-3">
            <h4>Aired On: </h4>
            <ul>
                {
                    aired.map((aired) => {
                        return (
                            <>
                                <li>{aired}</li>
                            </>
                        )
                    })
                }
            </ul>
        </div>
        
        <h3>Episode: {episode} episodes</h3>
        
        <h4>Synopsis: </h4>
        <p>{synopsis}</p>
        <h4>Genre: </h4>
        <ul>
            {
                genre.map((genre) => {
                    return (
                        <>
                            <li>{genre}</li>
                        </>
                    )
                })
            }
        </ul>
        <h5>Production Companies: </h5>
        <p>{studio}</p>
    </>
  )
}

export default Informasi